function getFirstItemID() {
					                     var IDs = MakeSelectionStr()
                                   arrIDs = IDs.split(';');
                               var result = '';
                               if (arrIDs.length > 0)
                               {
                                 result = arrIDs[0];
                               }
                               
                               return result;
				                     }
